command:
test -f "/etc/pgbouncer/auth/pgbouncer-admin-password"
test -f "/etc/pgbouncer/auth/pgbouncer-stats-password"
PGBOUNCER_ADMIN_MD5="$({
  cat "/etc/pgbouncer/auth/pgbouncer-admin-password"
  printf 'pgbouncer_admin'
  } | md5sum | cut -d ' ' -f 1)"
PGBOUNCER_STATS_MD5="$({
  cat "/etc/pgbouncer/auth/pgbouncer-stats-password"
  printf 'pgbouncer_stats'
  } | md5sum | cut -d ' ' -f 1)"
(
echo "\"pgbouncer_admin\" \"md5$PGBOUNCER_ADMIN_MD5\""
echo "\"pgbouncer_stats\" \"md5$PGBOUNCER_STATS_MD5\""
) > "/etc/pgbouncer/auth/users.txt"
